$.isBlank=function(n){return $.isPlainObject(n)&&$.isEmptyObject(n)||$.isArray(n)&&n.length===0||typeof n=="string"&&$.trim(n)===""||!n};$(document).ready(function(){$("input:file").change(function(){$.isBlank($(this).val())?$("input[type='submit']").attr("disabled","disabled"):$("input[type='submit']").removeAttr("disabled")})});